        ...userQueries,
        ...doctorQueries,
        ...patientQueries,
        ...locationQueries
    },
    Mutation: {
        ...userMutations,
        ...doctorMutations,
        ...patientMutations,
        ...locationMutations
    }
};

export default resolvers;

