import userMutations from "../user/mutations";
import {locationSchema} from "../../../Utils/LocationValidator";
import { Location} from "../../../Models";
import {ApolloError} from "apollo-server-errors";

const locationMutations = {

    /**
     * function to create new Location
     * @param _
     * @param location
     * @returns {Promise<ApolloError|*>}
     *      success -> Create new location
     *      failure -> return validation errors
     */
    createLocation: async (_, {location}) => {
        try{
            await locationSchema.validate(location, {abortEarly: false});
            const saveLocation = new Location(location);
            return saveLocation.save();
        } catch (err) {
            var error = err.inner && err.inner.length ? err.inner[0].message : err;
            return new ApolloError(error);
        }
    },

    /**
     * function to update Location details
     * @param _
     * @param id
     * @param location
     * @returns {Promise<*|ApolloError>}
     *      success -> update location details
     *      failure -> return validation errors
     */
    updateLocation: async (_, {id, location}) => {
        try{
            await locationSchema.validate(location, {abortEarly: false});

            const locationDetails = await Location.findOne({_id : id});
            if(!locationDetails){
                throw new ApolloError('Invalid location details.');
            }

            return await Location.findOneAndUpdate({
                _id: id
            }, {
                $set: location
            }, { returnOriginal: false });
        } catch (err) {
            var error = err.inner && err.inner.length ? err.inner[0].message : err;
            return new ApolloError(error);
        }
    }
}

export default locationMutations;

