        try{
            await updateDoctorSchema.validate(doctor, {abortEarly: false});

            const doctorDetails = await Doctor.findOne({_id : id});
            if(!doctorDetails){
                throw new ApolloError('Invalid doctor details.');
            }

            return await Doctor.findOneAndUpdate({
                _id: id
            }, {
                $set: doctor
            }, { returnOriginal: false })
        } catch (err) {
            var error = err.inner && err.inner.length ? err.inner[0].message : err;
            return new ApolloError(error);
        }
    }
}

export default doctorMutations;

