import {Machine, Location} from './../../../Models'
import {ApolloError, AuthenticationError} from 'apollo-server-errors';
import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import {createMachineSchema, updateMachineSchema} from '../../../Utils/MachineValidatior';

const CryptoJS = require("crypto-js");
const nodeBase64 = require('nodejs-base64-converter');

const machineMutations = {

    /**
     * function to create new machine
     * @param _
     * @param machine
     * @returns {Promise<ApolloError|*>}
     *      success - create new machine
     *      failure - return validation error
     */
    createMachine: async (_, {machine}) => {
        try{
            await createMachineSchema.validate(machine, {abortEarly: false});
            machine.status = true;
            const saveMachine = new Machine(machine);
            return saveMachine.save();
        } catch (err) {
            var error = err.inner && err.inner.length ? err.inner[0].message : err;
            return new ApolloError(error);
        }
    },

    /**
     * function to update the machine's
     * @param _
     * @param machine
     * @returns {Promise<ApolloError>}
     *      success - update the machine's profile info
 *          failure - validation errors
     */
    updateMachine: async (_, {id, machine}) => {
        try{
            await updateMachineSchema.validate(machine, {abortEarly: false});

            const machineDetails = await Machine.findOne({_id : id});
            if(!machineDetails){
                throw new ApolloError('Invalid machine details.');
            }

            return await Machine.findOneAndUpdate({
                _id: id
            }, {
                $set: machine
            }, { returnOriginal: false });
        } catch (err) {
            var error = err.inner && err.inner.length ? err.inner[0].message : err;
            return new ApolloError(error);
        }
    }
}

export default machineMutations;

